#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/use/bin/env$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/use/bin/env$exe" node "$basedir/../my-simple-cli-calculator/Calculator.js" $args
  } else {
    & "$basedir/use/bin/env$exe" node "$basedir/../my-simple-cli-calculator/Calculator.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "use/bin/env$exe" node "$basedir/../my-simple-cli-calculator/Calculator.js" $args
  } else {
    & "use/bin/env$exe" node "$basedir/../my-simple-cli-calculator/Calculator.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
